            mstore(0x40, keccak256(returndatasize(), 0x80)) // Compute and store the struct hash.
            // Layout the fields of the domain separator.
            mstore(0x60, _DOMAIN_TYPEHASH)
            mstore(0x80, _NAME_HASH)
            mstore(0xa0, _VERSION_HASH)
