                let t := staticcall(gas(), signer, 0x00, add(signature.length, 0x64), 0x24, 0x20)
                if iszero(and(eq(mload(0x24), f), t)) {
                    mstore(0x00, 0x8baa579f) // `InvalidSignature()`.
