    function eip712Domain()
        external
        view
        returns (
            bytes1 fields,
            string memory name,
            string memory version,
            uint256 chainId,
            address verifyingContract,
            bytes32 salt,
            uint256[] memory extensions
        )
    {
        assembly {
            pop(fields)
            mstore8(returndatasize(), 0x0f)
            pop(name)
            mstore(0x20, 0xe0)
            mstore(0xf5, 0x154d756c746963616c6c6572576974685369676e6572)
