            mstore(0x40, 0x120)
            mstore(0x121, 0x0131)
            pop(chainId)
            mstore(0x60, chainid())
            pop(verifyingContract)
            mstore(0x80, address())
            pop(salt)
            pop(extensions)
            mstore(0xc0, 0x160)
            return(returndatasize(), 0x180)
        }
    }
}

